const items = $input.all();
if (!items || items.length === 0) {
  return [{ json: { title: "No topics found", description: "Default description" } }];
}

const top5 = items.slice(0, 5);
const chosen = top5[Math.floor(Math.random() * top5.length)];

const title = chosen.json.title || "Trending Topic";
const description = chosen.json.contentSnippet || chosen.json.content || `Trending topic: ${title}` || "Popular topic from Google Trends India";

// Clean and enhance the topic
const cleanTitle = title.replace(/[^a-zA-Z0-9\s-]/g, '').trim();
const enhancedDescription = description.length > 200 ? description.substring(0, 200) + '...' : description;

return [{ 
  json: { 
    title: cleanTitle, 
    description: enhancedDescription,
    link: chosen.json.link || "#",
    originalTitle: title,
    category: "Trending",
    tags: ["trending", "india", "news"]
  } 
}];

