parameters.jsCode:
// Extract the topic for image generation
const title = $node["Enhanced Topic Selector"].json.title || "trending topic";
const description = $node["Enhanced Topic Selector"].json.description || "";

// Create a focused image prompt
const imagePrompt = `Professional blog header image for: ${title}. Modern, clean design, high quality, relevant imagery, suitable for blog post, vibrant colors, engaging composition`;

// Clean the prompt for URL encoding
const cleanPrompt = imagePrompt.replace(/[^a-zA-Z0-9\s,.-]/g, '').trim();

return {
  json: {
    imagePrompt: cleanPrompt,
    title: title,
    description: description
  }
};

// Get all the data from previous nodes
const topicData = $node["Enhanced Topic Selector"].json;
const contentData = $node["AI Content Generator"].json;
const imageData = $node["Image Prompt Generator"].json;

// Extract content from the AI response
let content = "";
if (contentData.body) {
  content = contentData.body;
} else if (contentData.data) {
  content = contentData.data;
} else if (contentData.text) {
  content = contentData.text;
} else if (typeof contentData === 'string') {
  content = contentData;
} else {
  content = JSON.stringify(contentData);
}

// Clean and format the content
if (typeof content === 'object') {
  content = JSON.stringify(content);
}

// Ensure content is properly formatted
if (!content.includes('##')) {
  content = `## Introduction\n\n${content}\n\n## Conclusion\n\nThis trending topic continues to capture attention across India, reflecting the dynamic nature of our digital landscape.`;
}

// Generate metadata
const date = new Date().toISOString().split('T')[0];
const slug = topicData.title.replace(/[^a-z0-9]/gi, '_').toLowerCase().substring(0, 50);
const filename = `${slug}.mdx`;
const imageFilename = `${slug}.jpg`;

// Create comprehensive MDX with frontmatter
const mdx = `---
title: "${topicData.title}"
date: "${date}"
description: "${topicData.description}"
slug: "${slug}"
featuredImage: "/images/blog/${imageFilename}"
category: "${topicData.category}"
tags: [${topicData.tags.map(tag => `"${tag}"`).join(', ')}]
author: "AI Blog Assistant"
readTime: "5 min read"
seo:
  metaTitle: "${topicData.title} | Latest Trends from India"
  metaDescription: "${topicData.description}"
  keywords: [${topicData.tags.map(tag => `"${tag}"`).join(', ')}]
published: true
---

![${topicData.title}](/images/blog/${imageFilename})

${content}

---

*This post was generated using AI to bring you the latest trending topics from India. Stay updated with the most relevant news and discussions.*`;

// Convert to Buffer for file writing
const buffer = Buffer.from(mdx, 'utf8');
const blogUrl = `https://your-blog-domain.com/blogs/${slug}`;

return {
  json: {
    mdx: mdx,
    title: topicData.title,
    filename: filename,
    imageFilename: imageFilename,
    blogUrl: blogUrl,
    description: topicData.description,
    slug: slug,
    contentPreview: content.substring(0, 200) + '...',
    wordCount: content.split(' ').length,
    tags: topicData.tags.join(', ')
  },
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'text/markdown',
      fileName: filename
    }
  }
};

// Error handling and logging
const errors = [];
const warnings = [];

// Check if content was generated properly
if (!$node["AI Content Generator"].json || Object.keys($node["AI Content Generator"].json).length === 0) {
  errors.push("Content generation failed");
}

// Check if image was generated
if (!$node["Featured Image Generator"].binary) {
  warnings.push("Image generation may have failed");
}

// Log results
console.log('Workflow Execution Summary:', {
  topic: $node["Enhanced Topic Selector"].json.title,
  contentGenerated: !!$node["AI Content Generator"].json,
  imageGenerated: !!$node["Featured Image Generator"].binary,
  errors: errors,
  warnings: warnings
});

return {
  json: {
    status: errors.length > 0 ? 'error' : 'success',
    errors: errors,
    warnings: warnings,
    summary: {
      topic: $node["Enhanced Topic Selector"].json.title,
      contentGenerated: !!$node["AI Content Generator"].json,
      imageGenerated: !!$node["Featured Image Generator"].binary
    }
  }
};
